// Extract challenge data and validate with enhanced categorization
const challenge = $json;

// Validate required fields
const requiredFields = ['challengerId', 'templateId', 'claimId', 'stakeAmount'];
const missingFields = requiredFields.filter(field => !challenge[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing required challenge fields: ${missingFields.join(', ')}`);
}

// Enhanced challenge severity calculation based on multiple factors
let severity = 'low';
const stakeThreshold = parseFloat(challenge.stakeAmount);
const reputationDiff = Math.abs((challenge.challengerReputation || 0) - (challenge.creatorReputation || 0));
const templateUsage = challenge.templateUsageCount || 0;

// Multi-factor severity calculation
if (stakeThreshold > 50000 || templateUsage > 10000 || reputationDiff > 40) {
  severity = 'critical';
} else if (stakeThreshold > 10000 || templateUsage > 1000 || reputationDiff > 20) {
  severity = 'high';
} else if (stakeThreshold > 1000 || templateUsage > 100 || reputationDiff > 10) {
  severity = 'medium';
}

// Generate deterministic challenge ID
const challengeId = challenge.challengeId || `chal_${challenge.claimId}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// Risk assessment
const riskFactors = {
  highStake: stakeThreshold > 10000,
  popularTemplate: templateUsage > 1000,
  reputationGap: reputationDiff > 20,
  firstTimeChallenger: (challenge.challengerReputation || 0) < 10
};

const riskCount = Object.values(riskFactors).filter(Boolean).length;
const requiresConsensus = severity === 'critical' || riskCount >= 2;

return {
  json: {
    challengeId,
    challengerId: challenge.challengerId,
    challengerAddress: challenge.challengerAddress,
    templateId: challenge.templateId,
    claimId: challenge.claimId,
    claimText: challenge.claimText,
    challengeReason: challenge.challengeReason,
    stakeAmount: challenge.stakeAmount,
    severity,
    requiresConsensus,
    createdAt: new Date().toISOString(),
    metadata: {
      challengerReputation: challenge.challengerReputation || 0,
      creatorReputation: challenge.creatorReputation || 0,
      templateUsageCount: templateUsage,
      reputationDiff,
      riskFactors,
      riskScore: riskCount
    }
  }
};

// Determine challenge outcome with enhanced scoring algorithm
const originalClaim = $node['verify-original-claim'].json;
const challengeClaim = $node['verify-challenge-claim'].json;
const challengeData = $node['prepare-challenge'].json;
const consensus = $node['multi-agent-consensus']?.json;

// Use consensus if available, otherwise use direct comparison
let winner = 'original';
let confidence = 0.5;
let reasoning = 'Default outcome';

if (consensus && consensus.success) {
  // Multi-agent consensus available
  winner = consensus.metrics.consensus === 'challenger' ? 'challenger' : 'original';
  confidence = consensus.metrics.confidence;
  reasoning = `Multi-agent consensus: ${consensus.metrics.reasoning || 'Consensus reached'}`;
} else {
  // Direct verification comparison
  const originalConfidence = originalClaim.metrics?.confidence || 0;
  const challengeConfidence = challengeClaim.metrics?.confidence || 0;
  const confidenceDiff = challengeConfidence - originalConfidence;

  // Enhanced scoring with source quality and reputation factors
  const originalSourceQuality = originalClaim.metrics?.sourceQuality || 0;
  const challengeSourceQuality = challengeClaim.metrics?.sourceQuality || 0;
  const sourceQualityDiff = challengeSourceQuality - originalSourceQuality;

  // Reputation factor (higher rep = slight bias towards their claims)
  const repFactor = (challengeData.metadata.challengerReputation - challengeData.metadata.creatorReputation) * 0.01;

  // Combined score
  const combinedScore = confidenceDiff + (sourceQualityDiff * 0.3) + repFactor;

  if (combinedScore > 0.15) {
    winner = 'challenger';
    confidence = challengeConfidence;
    reasoning = 'Challenge provides superior evidence and sources';
  } else if (combinedScore < -0.15) {
    winner = 'original';
    confidence = originalConfidence;
    reasoning = 'Original claim has stronger foundation';
  } else {
    // Very close - favor slightly based on source quality
    if (sourceQualityDiff > 0.1) {
      winner = 'challenger';
      confidence = challengeConfidence;
      reasoning = 'Challenge has significantly better source quality';
    } else {
      winner = 'original';
      confidence = originalConfidence;
      reasoning = 'Claims too close to determine clear winner - original stands';
    }
  }
}

return {
  json: {
    challengeId: challengeData.challengeId,
    winner: winner,
    confidence: confidence,
    reasoning: reasoning,
    originalScore: originalClaim.metrics?.confidence || 0,
    challengeScore: challengeClaim.metrics?.confidence || 0,
    consensusUsed: Boolean(consensus && consensus.success),
    metadata: {
      severity: challengeData.severity,
      riskScore: challengeData.metadata.riskScore,
      reputationDiff: challengeData.metadata.reputationDiff
    }
  }
};

// Calculate quadratic stake redistribution with reputation scaling
const outcome = $json;
const challengeData = $node['prepare-challenge'].json;
const stakeAmount = parseFloat(challengeData.stakeAmount);

// Get reputation scores
const challengerRep = challengeData.metadata.challengerReputation;
const creatorRep = challengeData.metadata.creatorReputation;

// Base reputation changes (scaled by confidence)
let challengerRepChange = 0;
let creatorRepChange = 0;

// Stake redistribution with quadratic scaling
let challengerPayout = 0;
let creatorPayout = 0;
let treasuryPayout = 0;

// Quadratic scaling function - prevents whale dominance
const applyQuadraticScaling = (amount, reputation) => {
  // Higher reputation = less scaling (more trusted)
  const trustFactor = Math.min(1, reputation / 100);
  const scaleFactor = Math.sqrt(1 - (0.5 * trustFactor));
  return amount * scaleFactor;
};

if (outcome.winner === 'challenger') {
  // Challenger wins - gets stake plus quadratic bonus
  const baseReward = stakeAmount * 1.8; // 80% bonus
  challengerPayout = applyQuadraticScaling(baseReward, challengerRep);

  // Reputation changes
  challengerRepChange = Math.min(15, 5 + (outcome.confidence * 10));
  creatorRepChange = -Math.min(10, 3 + (outcome.confidence * 7));

  treasuryPayout = stakeAmount * 0.1; // 10% to treasury

} else {
  // Original creator wins - gets challenger's stake
  const baseReward = stakeAmount * 1.3; // 30% bonus for defending truth
  creatorPayout = applyQuadraticScaling(baseReward, creatorRep);

  // Reputation changes (less severe for failed challenges)
  challengerRepChange = -Math.min(8, 2 + (outcome.confidence * 6));
  creatorRepChange = Math.min(8, 2 + (outcome.confidence * 6));

  treasuryPayout = stakeAmount * 0.2; // 20% to treasury
}

// Apply consensus multiplier if multi-agent consensus was used
if (outcome.consensusUsed) {
  challengerRepChange *= 1.5;
  creatorRepChange *= 1.5;
  challengerPayout *= 1.1;
  creatorPayout *= 1.1;
}

// Apply severity multiplier
const severityMultipliers = { low: 1, medium: 1.2, high: 1.5, critical: 2 };
const severityMultiplier = severityMultipliers[challengeData.severity] || 1;

challegerRepChange *= severityMultiplier;
creatorRepChange *= severityMultiplier;

return {
  json: {
    challengeId: challengeData.challengeId,
    winner: outcome.winner,
    confidence: outcome.confidence,
    reasoning: outcome.reasoning,
    payouts: {
      challenger: Math.round(challengerPayout),
      creator: Math.round(creatorPayout),
      treasury: Math.round(treasuryPayout)
    },
    reputationChanges: {
      challenger: Math.round(challengerRepChange * 100) / 100,
      creator: Math.round(creatorRepChange * 100) / 100
    },
    metadata: {
      stakeAmount,
      severity: challengeData.severity,
      consensusUsed: outcome.consensusUsed,
      quadraticScaling: true,
      severityMultiplier
    }
  }
};

