// Prepare final response with comprehensive challenge resolution data
const resolution = $json;
const reputationUpdate = $node['update-reputation']?.json;
const dbStorage = $node['store-resolution']?.json;

return {
  json: {
    success: true,
    challengeId: resolution.challengeId,
    status: 'resolved',
    winner: resolution.winner,
    message: `Challenge resolved: ${resolution.winner} wins with ${(resolution.confidence * 100).toFixed(0)}% confidence`,
    resolution: {
      winner: resolution.winner,
      confidence: resolution.confidence,
      reasoning: resolution.reasoning,
      severity: resolution.metadata.severity,
      consensusUsed: resolution.metadata.consensusUsed
    },
    economics: {
      payouts: resolution.payouts,
      reputationChanges: resolution.reputationChanges,
      quadraticScaling: resolution.metadata.quadraticScaling,
      treasuryContribution: resolution.payouts.treasury
    },
    processing: {
      stored: Boolean(dbStorage?.success),
      reputationUpdated: Boolean(reputationUpdate?.success),
      notificationSent: true
    },
    timestamp: new Date().toISOString(),
    philosophy: 'Quality discourse pays. Bad faith costs.'
  }
};
