// Extract and categorize error information
const error = $json;

// Determine error severity
let severity = 'low';
let category = 'general';
let isRetryable = false;
let alertRequired = false;

const errorPatterns = {
  // Critical errors - require immediate attention
  critical: [
    /database.*connection.*lost/i,
    /authentication.*failed/i,
    /rate.*limit.*exceeded/i,
    /out.*of.*memory/i,
    /smart.*contract.*reverted/i
  ],
  // High severity - may affect users
  high: [
    /timeout/i,
    /verification.*failed/i,
    /insufficient.*funds/i,
    /invalid.*signature/i
  ],
  // Medium severity - degraded performance
  medium: [
    /retry.*limit.*reached/i,
    /partial.*failure/i,
    /queue.*full/i
  ]
};

// Check error message against patterns
const errorMessage = error.message || error.error || JSON.stringify(error);

for (const [level, patterns] of Object.entries(errorPatterns)) {
  if (patterns.some(pattern => pattern.test(errorMessage))) {
    severity = level;
    alertRequired = level === 'critical';
    break;
  }
}

// Categorize error type
if (errorMessage.includes('database') || errorMessage.includes('postgres')) {
  category = 'database';
  isRetryable = true;
} else if (errorMessage.includes('agent') || errorMessage.includes('API')) {
  category = 'agent_communication';
  isRetryable = true;
} else if (errorMessage.includes('smart contract') || errorMessage.includes('blockchain')) {
  category = 'blockchain';
  isRetryable = false;
} else if (errorMessage.includes('verification') || errorMessage.includes('validation')) {
  category = 'validation';
  isRetryable = false;
} else if (errorMessage.includes('timeout') || errorMessage.includes('network')) {
  category = 'network';
  isRetryable = true;
}

// Build error context
const errorContext = {
  id: `error_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  timestamp: new Date().toISOString(),
  severity,
  category,
  isRetryable,
  alertRequired,
  workflow: {
    id: error.workflowId || 'unknown',
    name: error.workflowName || 'unknown',
    executionId: error.executionId || 'unknown'
  },
  error: {
    message: errorMessage,
    code: error.code || 'UNKNOWN',
    stack: error.stack || null,
    details: error
  },
  metadata: {
    nodeId: error.nodeId || null,
    nodeName: error.nodeName || null,
    attemptNumber: error.attemptNumber || 1,
    maxAttempts: error.maxAttempts || 3
  }
};

return { json: errorContext };

