// Prepare retry configuration based on error type
const error = $json;

// Calculate exponential backoff
const attemptNumber = error.metadata.attemptNumber || 1;
const baseDelay = 1000; // 1 second
const maxDelay = 60000; // 1 minute
const delay = Math.min(baseDelay * Math.pow(2, attemptNumber - 1), maxDelay);

// Add jitter to prevent thundering herd
const jitter = Math.random() * 0.3 * delay;
const finalDelay = Math.floor(delay + jitter);

// Determine retry strategy based on category
let retryStrategy = 'exponential_backoff';
let maxRetries = 3;

switch (error.category) {
  case 'database':
    maxRetries = 5;
    retryStrategy = 'exponential_backoff';
    break;
  case 'network':
    maxRetries = 10;
    retryStrategy = 'exponential_backoff_with_jitter';
    break;
  case 'agent_communication':
    maxRetries = 3;
    retryStrategy = 'linear_backoff';
    break;
  default:
    maxRetries = 3;
}

const shouldRetry = attemptNumber < maxRetries;

return {
  json: {
    ...error,
    retry: {
      shouldRetry,
      attemptNumber,
      maxRetries,
      delay: finalDelay,
      strategy: retryStrategy,
      nextAttempt: shouldRetry ? attemptNumber + 1 : null,
      scheduledFor: shouldRetry 
        ? new Date(Date.now() + finalDelay).toISOString()
        : null
    }
  }
};

// Send metrics to monitoring service
const error = $json;

// Prepare metrics
const metrics = {
  metric: 'workflow_error',
  value: 1,
  tags: [
    `severity:${error.severity}`,
    `category:${error.category}`,
    `workflow:${error.workflow.name}`,
    `retryable:${error.isRetryable}`
  ],
  timestamp: Math.floor(Date.now() / 1000)
};

// In production, this would send to DataDog/Prometheus
console.log('Metrics:', metrics);

// Track error patterns
const errorPattern = {
  hour: new Date().getHours(),
  category: error.category,
  count: 1
};

return {
  json: {
    metricsLogged: true,
    metrics,
    errorPattern
  }
};

// Aggregate error information for response
const error = $node['categorize-error'].json;
const retryInfo = $node['prepare-retry']?.json?.retry || null;
const alertSent = $node['send-critical-alert']?.json?.success || false;
const loggedToDb = $node['log-to-database']?.json?.[0]?.success !== false;
const addedToDlq = $node['add-to-dlq']?.json?.success !== false;

return {
  json: {
    success: true,
    errorId: error.id,
    message: 'Error handled successfully',
    actions: {
      alertSent,
      loggedToDatabase: loggedToDb,
      addedToDeadLetterQueue: addedToDlq && !retryInfo?.shouldRetry,
      retryScheduled: retryInfo?.shouldRetry || false
    },
    error: {
      severity: error.severity,
      category: error.category,
      message: error.error.message
    },
    retry: retryInfo,
    timestamp: new Date().toISOString()
  }
};
