parameters.functionCode:
// Aggregate all metrics
const activity = $node['get-activity-metrics'].json[0];
const rewards = $node['get-reward-metrics'].json[0];
const reputation = $node['get-reputation-metrics'].json[0];

// Calculate current supply metrics
const currentMetrics = {
  dailyActiveUsers: parseInt(activity.daily_active_users) || 0,
  totalActions: parseInt(activity.total_actions) || 0,
  congressionalRatio: parseFloat(activity.congressional_ratio) || 0,
  dailyRewards: parseFloat(rewards.daily_rewards) || 0,
  avgReward: parseFloat(rewards.avg_reward) || 0,
  maxReward: parseFloat(rewards.max_reward) || 0,
  minReward: parseFloat(rewards.min_reward) || 0,
  avgChallengeScore: parseFloat(reputation.avg_challenge) || 50,
  avgCivicScore: parseFloat(reputation.avg_civic) || 50,
  avgDiscourseScore: parseFloat(reputation.avg_discourse) || 50,
  trustedUsers: parseInt(reputation.trusted_users) || 0
};

// Check thresholds
const thresholdsExceeded = [];

// High activity threshold
if (currentMetrics.dailyActiveUsers > 1000) {
  thresholdsExceeded.push('high_activity');
}

// Low activity threshold
if (currentMetrics.dailyActiveUsers < 10) {
  thresholdsExceeded.push('low_activity');
}

// Reward inflation threshold
if (currentMetrics.dailyRewards > 1000000) {
  thresholdsExceeded.push('reward_inflation');
}

// Reputation concentration threshold
if (currentMetrics.trustedUsers > currentMetrics.dailyActiveUsers * 0.5) {
  thresholdsExceeded.push('reputation_concentration');
}

return {
  json: {
    timestamp: new Date().toISOString(),
    metrics: currentMetrics,
    thresholdsExceeded: thresholdsExceeded,
    optimizationNeeded: thresholdsExceeded.length > 0
  }
};

// Calculate optimized parameters based on metrics
const metrics = $json.metrics;
const thresholds = $json.thresholdsExceeded;

// Base parameters (would come from AgentParameters contract)
let baseReward = 10 * Math.pow(10, 18); // 10 VOTER
let verificationThreshold = 0.7;
let stakingAPR = 10;

// Supply agent logic
let supplyMultiplier = 1.0;
if (thresholds.includes('high_activity')) {
  supplyMultiplier = 0.7; // Reduce rewards during high activity
} else if (thresholds.includes('low_activity')) {
  supplyMultiplier = 1.5; // Increase rewards during low activity
}

// Market agent logic
let marketMultiplier = 1.0;
if (metrics.congressionalRatio > 0.7) {
  marketMultiplier = 1.2; // Bonus for congressional engagement
}

// Impact agent logic
let impactMultiplier = 1.0;
if (metrics.avgCivicScore > 70) {
  impactMultiplier = 1.1; // Bonus for high civic participation
}

// Calculate optimized parameters
const optimizedReward = Math.floor(baseReward * supplyMultiplier * marketMultiplier * impactMultiplier);
const optimizedThreshold = Math.min(0.9, Math.max(0.5, verificationThreshold + (metrics.avgDiscourseScore - 50) / 100));
const optimizedAPR = Math.min(20, Math.max(5, stakingAPR * (100 - metrics.dailyActiveUsers) / 100));

// Safety rails (min/max bounds)
const MIN_REWARD = 1 * Math.pow(10, 18); // 1 VOTER
const MAX_REWARD = 100 * Math.pow(10, 18); // 100 VOTER
const MIN_THRESHOLD = 0.5;
const MAX_THRESHOLD = 0.9;
const MIN_APR = 5;
const MAX_APR = 20;

return {
  json: {
    recommendations: {
      baseReward: {
        current: baseReward,
        optimized: Math.min(MAX_REWARD, Math.max(MIN_REWARD, optimizedReward)),
        change: ((optimizedReward - baseReward) / baseReward * 100).toFixed(2) + '%'
      },
      verificationThreshold: {
        current: verificationThreshold,
        optimized: Math.min(MAX_THRESHOLD, Math.max(MIN_THRESHOLD, optimizedThreshold)),
        change: ((optimizedThreshold - verificationThreshold) / verificationThreshold * 100).toFixed(2) + '%'
      },
      stakingAPR: {
        current: stakingAPR,
        optimized: Math.min(MAX_APR, Math.max(MIN_APR, optimizedAPR)),
        change: ((optimizedAPR - stakingAPR) / stakingAPR * 100).toFixed(2) + '%'
      }
    },
    rationale: {
      supply: `Supply multiplier: ${supplyMultiplier.toFixed(2)}x based on ${metrics.dailyActiveUsers} DAU`,
      market: `Market multiplier: ${marketMultiplier.toFixed(2)}x based on ${(metrics.congressionalRatio * 100).toFixed(0)}% congressional ratio`,
      impact: `Impact multiplier: ${impactMultiplier.toFixed(2)}x based on ${metrics.avgCivicScore.toFixed(0)} avg civic score`
    },
    governanceRequired: Math.abs(optimizedReward - baseReward) / baseReward > 0.2 // >20% change needs governance
  }
};

// Format governance proposal
const recommendations = $json.recommendations;
const rationale = $json.rationale;
const metrics = $node['analyze-metrics'].json.metrics;

const proposal = {
  title: `Supply Optimization Proposal - ${new Date().toISOString().split('T')[0]}`,
  description: `Automated supply optimization based on network metrics`,
  changes: [
    {
      parameter: 'BASE_REWARD',
      current: recommendations.baseReward.current,
      proposed: recommendations.baseReward.optimized,
      rationale: rationale.supply
    },
    {
      parameter: 'VERIFICATION_THRESHOLD',
      current: recommendations.verificationThreshold.current,
      proposed: recommendations.verificationThreshold.optimized,
      rationale: `Adjusted based on discourse quality metrics`
    },
    {
      parameter: 'STAKING_APR',
      current: recommendations.stakingAPR.current,
      proposed: recommendations.stakingAPR.optimized,
      rationale: `Optimized for ${metrics.dailyActiveUsers} daily active users`
    }
  ],
  metrics: metrics,
  votingPeriod: 86400, // 24 hours
  executionDelay: 43200 // 12 hours after vote
};

return {
  json: {
    proposalId: `prop-${Date.now()}`,
    proposal: proposal,
    status: 'draft'
  }
};

